import axios from 'axios';
import setAlert from './aleart';
import setAuthToken from '../utils/isAuthantication';
import Aleart from '../component/layout/Aleart';
import { BUSNOT_FOUND, BUS_FOUND } from './type';

// Search bus
export const searchbus = ({ from,to, date}) => async dispatch=>{
    const config = {
        headers: { "Content-Type": "application/json" }
    }
    const start = from.trim().split(',')
    const End = to.trim().split(',')

    from = { "city": start[0], "state": start[1] }
    to = { "city": End[0], "state": End[1] }

    // const [toCity, toState] = to.trim().split(',')
    // const [fromCity, fromState] = from.trim().split(',')
    // const data = {
    //     "source": {
    //         "city": toCity,
    //         "state": toState
    //     },
    //     "destination": {
    //         "city": fromCity,
    //         "state": fromState
    //     },
    //     date
    // }
    // const body = JSON.stringify({ from, to, date });
    try {
        const res = await axios.post('http://localhost:1900/api/admins/', {from,to,date}, config);
        console.log(res)
        dispatch({
            type: BUS_FOUND,
            payload: res.data
        });
        alert("Bus Found")
    } catch (err) {
        const errors = err.response.data.err;
        if (errors) {
            alert("Not Found")
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }
        dispatch({
            type: BUSNOT_FOUND
        })
    }
}
