import axios from 'axios';
import setAlert from './aleart';
import setAuthToken from '../utils/isAuthantication';
import Aleart from '../component/layout/Aleart';


// Search bus
export const searchbus = ({ start, destination, date}) => async dispatch=>{
    const config = {
        headers: { "Content-Type": "application/json" }
    }
    const body = JSON.stringify({start,destination, date});
    try {
        const res = await axios.post('http://localhost:1900/api/admins/signup', body, config);
        console.log(res)
        dispatch({
            type: BUS,
            payload: res.data
        });
        alert("Bus Found")
    } catch (err) {
        const errors = err.response.data.err;
        if (errors) {
            alert("Not Found")
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }
        dispatch({
            type: REGISTER_FAIL
        })
    }
}
