import React, { useState, useEffect, Fragment } from "react";
import { useHistory, Link } from 'react-router-dom';
import PropTypes from "prop-types";
import { bookSeats } from '../../action/Bookseat';
// import PassengerDetail from "./passangerDetails";
import { getBusStatus } from '../../action/busStatus';
import { connect, connectAdvanced } from "react-redux";

const BookTicket = ({ bookSeats, buses, match, busStatus: { loading, status }, getBusStatus }) => {
    useEffect(() => {
        getBusStatus(match.params.busId);
    }, getBusStatus[match.params.busId]);

    const [name, setName] = useState([]);
    const [email, setEmail] = useState([]);
    const [phone, setPhone] = useState([]);
    const [age, setAge] = useState([]);
    const [gender, setGender] = useState([]);
    const [isok, setIsok] = useState("false");
    const [seatNumber, setSeatnumber] = useState([]);
    const travelDate = localStorage.getItem("travelDate");
    const history = useHistory();

    const getSeatNumber = (e) => {
        let newSeat = e.target.id;
        console.log("seat", newSeat)
        if (seatNumber.includes(newSeat)) { setSeatnumber(seatNumber.filter((seat) => seat !== newSeat)); }
        setSeatnumber([...seatNumber, newSeat])
    };

    const Gender = (e) => {
        const gender = e.target.name.value;
        console.log("gender", gender)
        setGender(gender.concat(gender));
    };

    const PassengerName = (e) => {
        e.preventDefault();
        let value = e.target.name.value;
        console.log("name", value)
        if (!value) { return setName("name is required") }
        setName(name.concat(value))
    };

    const PassengerEmail = (e) => {
        e.preventDefault();
        let value = e.target.name.value;
        console.log("email", value)
        if (!value) { return setEmail("Email is required") }
        setEmail(email.concat(value))
    };

    const PassengerPhone = (e) => {
        e.preventDefault();
        let value = e.target.id.value;
        console.log("phone", value)
        if (!value) { return setPhone("Phone no. is required") }
        setPhone(phone.concat(value))
    };

    const PassengerAge = (e) => {
        e.preventDefault();
        let value = e.target.name.va;
        console.log("Age", value)
        if (!value) { return setAge("Age is required") }
        setAge(age.concat(value))
    }

    const SubmitDetails = (e) => {
        e.preventDefault();
        const passengers = []
        for (let i = 0; i < seatNumber.length; i++) {
            const newSeat = seatNumber[i]
            if (name[newSeat] || age[newSeat] || gender[newSeat] !== "Empty") {
                passengers.push({ name: name[newSeat], age: age[newSeat], gender: gender[newSeat] })
            }
            else { return alert("Please fill all the details") }
        }
        let userData = {
            seats_no: seatNumber,
            passengers,
            journeyDate: travelDate,
            email,
            contactNo: phone
        }
        bookSeats(match.params.busId, userData)
    }

    const isBooked = (seat) => {
        if (!loading) {
            return status.hasOwnProperty(seat) ? seat : "booked"
        }
    }
    console.log("bus status")
    return (
        <div className="col-12">
            <div className="row">
                <div className="col-6">
                    {buses.map(bus => {
                        if (bus._id === match.params.busId) {
                            <div class="plane">
                                <h6>Please select seats</h6>
                                <form>
                                    <div rel="stylesheet">
                                        <div className="exit exit--front fuselage">
                                        </div>
                                        <ol className="cabin fuselage">
                                            <li className="col-3 mb-3">
                                                {/* {bus.seats.map(row => (
                                                    <Fragment>

                                                    </Fragment>   
                                                ) */}
                                                ) 
                                                }

                                            </li>
                                        </ol>

                                    </div>
                                </form>
                            </div>
                        }
                    }
                    )
                    }
                </div>
                <div className="col-6">
                    text2
                    {isok == "false" ? "" :
                        <div className="col-6">
                            <form className="form seatfrm ">
                                <p className="text-capitalize text-center">Seat No</p>
                                <input
                                    className="form-control seatInp"
                                    type="String"
                                    name="passenger-Name"
                                    placeholder="Enter Name"
                                    value={name}
                                    onClick={(e) => PassengerName(e)}
                                />
                                <br></br>
                                <input
                                    className="form-control seatInp"
                                    type="int"
                                    placeholder="Enter Age"
                                    name="passenger-age"
                                    value={age}
                                    onClick={(e) => PassengerName(e)}
                                />
                                <br></br>
                                <input
                                    className="form-control seatInp"
                                    type="int"
                                    placeholder="Enter Phone Number"
                                    name="passenger-PhoneNumber"
                                    value={phone}
                                    onClick={(e) => PassengerName(e)}
                                />
                                <input
                                    className="form-control seatInp"
                                    type="String"
                                    placeholder="Enter  email"
                                    name="passenger-Email"
                                    value={email}
                                    onClick={(e) => PassengerEmail(e)}
                                />
                                <div className="form-check form-check-inline">
                                    <input
                                        className="form-check-input"
                                        type="radio"
                                        name="gender"
                                        id="male"
                                        value="Male"
                                        onClick={(e) => Gender(e)}
                                    />
                                    <label className="form-check-label" for="male">
                                        Male
                                    </label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input
                                        className="form-check-input"
                                        type="radio"
                                        name="gender"
                                        id="female"
                                        value="Female"
                                        onClick={(e) => Gender(e)}
                                    />
                                    <label className="form-check-label" htmlFor="female">
                                        Female
                                    </label>
                                </div>
                            </form>
                            <div className="text-center">
                                <button className="btn btn-primary mt-3" type="submit" onClick={(e) => SubmitDetails()}>Book Tickets</button>
                            </div>
                        </div>}
                </div>
            </div>
        </div>
    )
}
BookTicket.propTypes = {
    buses: PropTypes.func.isRequired,
    bookSeats: PropTypes.func.isRequired,
    getBusStatus: PropTypes.func.isRequired,
    busStatus: PropTypes.func
};

const mapStateToProps = state => ({
    busStatus: state.busStatus,
    buses: state.searchBus.buses
})
export default connect(mapStateToProps, { bookSeats, getBusStatus })(BookTicket);
