import { ADD_BUS, ADD_BUS_FAILED } from './type';
import axios from 'axios';
import setAlert from './aleart';

const addBus = ({
    busName,
    vehicleNo,
    seats,
    busType,
    seatCategory,
    policy,
    from,
    to,
    arrivalTime,
    departureTime,
    image,
    staffs,
    secdule,
}) => async dispatch =>{
    const config = {
        headers: {
            'Content-Type':'application/json'
        }
    };

    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    console.log(scedule)
    for (let day = 0; day < scedule.length; day++) {
        scedule[day] = days.indexOf(scedule[day]);
    }

    const Staff_Number = [staffs];
    const body = JSON.stringify({
        busName,
        vehicleNo,
        seats,
        busType,
        seatCategory,
        policy,
        from,
        to,
        arrivalTime,
        departureTime,
        image,
        Staff_Number,
        scedule
    });
    try {
        const res = await axios.post('/api/admins/admin/Addbus', body, config);
        console.log(res)
        dispatch({
            type: ADD_BUS,
            payload: res.data
        });
        dispatch(alert('bus has added succefully', 'success'));
    } catch (err) {
        console.log(err);
        dispatch(alert('there was an error while adding the bus', 'danger'));

        dispatch({
            type: ADD_BUS_FAILED
        });

    }
    };

export default addBus
