import React, { useState, useEffect, Fragment } from "react";
import { useHistory } from 'react-router-dom';
import PropTypes from "prop-types";
import { bookSeats } from '../../action/Tickets';
import PassengerDetail from "./passangerDetails";
import { getBusStatus } from '../../action/busStatus';
import { connect } from "react-redux";

const BookTicket = ({ bookSeats, match, busStatus: { loading, status } }) => {
    console.log("start point")
    useEffect(() => {
        getBusStatus(match.params.busId);
    }, [getBusStatus, match.params.busId]);

    const [name, setName] = useState([]);
    const [email, setEmail] = useState([]);
    const [phone, setPhone] = useState([]);
    const [age, setAge] = useState([]);
    const [gender, setGender] = useState([]);
    const [reservedSeat, setReservedSeat] = useState([]);
    const [seatNumber, setSeatnumber] = useState([]);
    const travelDate = localStorage.getItem("travelDate");

    const history = useHistory();
    const getSeatNumber = (e) => {
        let newSeat = e.target.value;
        console.log("27",newSeat)
        history.push("/passengerForm");
        if (seatNumber.includes(newSeat)) {
            setSeatnumber(seatNumber.filter((seat) => seat !== newSeat));
        }
        else {
            setSeatnumber([...seatNumber, newSeat]);
        }
    };

    const handleGender = (e, seatNo) => {
        const { value } = e.target;
        setGender(gender.concat(value));
    };
    const handlePassengerName = (e, seatNo) => {
        e.preventDefault();
        let value = e.target.value;
        if (!value) {
            return setName("name is required");
        } else {
            setName(name.concat(value));
        }
    };
    const handlePassengerEmail = (e, seatNo) => {
        e.preventDefault();
        let value = e.target.value;
        if (!value) {
            return setEmail("Email is required");
        } else {
            setEmail(email.concat(value));
        }
    };
    console.log("cross", handlePassengerEmail)
    const handlePassengerPhone = (e, seatNo) => {
        e.preventDefault();
        let value = e.target.value;
        if (!value) {
            return setPhone("Phone no. is required");
        } else {
            setPhone(phone.concat(value));
        }
    };

    const handlePassengerAge = (e, seatNo) => {
        e.preventDefault();
        let value = e.target.value;
        if (!value) {
            return setAge("Age is required");
        } else {
            setAge(age.concat(value));
        }
    }
    const handleSubmitDetails = e => {
        e.preventDefault()
        const passengers = []
        for (let i = 0; i < seatNumber.length; i++) {
            const currentSeat = seatNumber[i]
            if (name[currentSeat] || age[currentSeat] || gender[currentSeat] !== "Empty") {
                passengers.push({
                    name: name[currentSeat],
                    age: age[currentSeat],
                    gender: gender[currentSeat]
                })
            }
            else {
                return alert("Please fill all the details")
            }

        }
        let userData = {
            seats_no: seatNumber,
            passengers,
            journeyDate: travelDate,
            email,
            contactNo: phone
        }

        bookSeats(match.params.busId, userData)
    }
    const renderPassengerData = (seatArray)=>{
        console.log(seatNumber, "hello")
        return seatArray.map((seat, idx) => {
            return (
