import { STATUS_ERROR, GET_BUSSTATUS } from "../action/types";

const initialState = {
    status: {},
    loading: true,
    error: {},
};

const busStatus =  (state = initialState, action) => {
    const { type, payload } = action;

    switch (type) {
        case GET_BUSSTATUS:
            return {
                ...state,
                status: payload,
                loading: false,
            };
        case STATUS_ERROR:
            return {
                ...state,
                error: payload,
                loading: false,
            };
        default:
            return state;
    }
};

export default busStatus;
